import React, { useState } from 'react';
import { Task, TaskFormData } from '../types/Task';
import { createTask } from '../services/api';

interface TaskFormProps {
  onTaskCreated: (task?: Task) => void;
}

const TaskForm: React.FC<TaskFormProps> = ({ onTaskCreated }) => {
  const [formData, setFormData] = useState<TaskFormData>({
    title: '',
    description: '',
    type: 'one-time',
    scheduledTime: undefined,
    cronExpression: '',
  });

  const [error, setError] = useState<string | null>(null);
  const [loading, setLoading] = useState<boolean>(false);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    setFormData((prev: TaskFormData) => ({ ...prev, [name]: value }));
  };

  const handleDateTimeChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { value } = e.target;
    setFormData((prev: TaskFormData) => ({ ...prev, scheduledTime: value ? new Date(value) : undefined }));
  };

  const handleSubmit = async (event: React.FormEvent) => {
    event.preventDefault();
    setError(null);
    setLoading(true);

    try {
      if (!formData.title) {
        setError('O título é obrigatório');
        return;
      }

      if (formData.type === 'one-time' && !formData.scheduledTime) {
        setError('O horário agendado é obrigatório para tarefas únicas');
        return;
      }

      if (formData.type === 'recurring' && !formData.cronExpression) {
        setError('A expressão cron é obrigatória para tarefas recorrentes');
        return;
      }

      let processedData = { ...formData };
      const createdTask = await createTask(processedData);

      onTaskCreated(createdTask);

      // Limpar o formulário
      setFormData({
        title: '',
        description: '',
        type: 'one-time',
        scheduledTime: new Date(),
        cronExpression: '',
      });
    } catch (error) {
      console.error("Erro ao criar tarefa:", error);
      setError('Falha ao criar tarefa. Por favor, tente novamente.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="box">
      <h2 className="title is-4">
        <span className="icon-text">
          <span className="icon">
            <i className="fas fa-calendar-plus"></i>
          </span>
          <span>Nova Tarefa</span>
        </span>
      </h2>

      {error && (
        <div className="notification is-danger is-light">
          <button className="delete" onClick={() => setError(null)}></button>
          {error}
        </div>
      )}

      <form onSubmit={handleSubmit}>
        <div className="field">
          <label className="label">Título</label>
          <div className="control has-icons-left">
            <input
              className="input"
              type="text"
              name="title"
              value={formData.title}
              onChange={handleChange}
              placeholder="Digite o título da tarefa"
            />
            <span className="icon is-small is-left">
              <i className="fas fa-tasks"></i>
            </span>
          </div>
        </div>

        <div className="field">
          <label className="label">Descrição</label>
          <div className="control">
            <textarea
              className="textarea"
              name="description"
              value={formData.description || ''}
              onChange={handleChange}
              placeholder="Descreva a tarefa (opcional)"
            />
          </div>
        </div>

        <div className="field">
          <label className="label">Tipo de Tarefa</label>
          <div className="control">
            <div className="select is-fullwidth">
              <select
                name="type"
                value={formData.type}
                onChange={handleChange}
              >
                <option value="one-time">Única</option>
                <option value="recurring">Recorrente</option>
              </select>
            </div>
          </div>
        </div>

        {formData.type === 'one-time' && (
          <div className="field">
            <label className="label">Data e Hora</label>
            <div className="control has-icons-left">
              <input
                className="input"
                type="datetime-local"
                name="scheduledTime"
                value={formData.scheduledTime ? new Date(formData.scheduledTime.getTime() - formData.scheduledTime.getTimezoneOffset() * 60000).toISOString().slice(0, 16) : ''}
                onChange={handleDateTimeChange}
              />
              <span className="icon is-small is-left">
                <i className="fas fa-clock"></i>
              </span>
            </div>
          </div>
        )}

        {formData.type === 'recurring' && (
          <div className="field">
            <label className="label">Expressão Cron</label>
            <div className="control has-icons-left">
              <input
                className="input"
                type="text"
                name="cronExpression"
                value={formData.cronExpression || ''}
                onChange={handleChange}
                placeholder="* * * * *"
              />
              <span className="icon is-small is-left">
                <i className="fas fa-sync"></i>
              </span>
            </div>
            <p className="help">Formato: minuto hora dia-do-mês mês dia-da-semana</p>
          </div>
        )}

        <div className="field">
          <div className="control">
            <button 
              className={`button is-primary ${loading ? 'is-loading' : ''}`}
              type="submit"
              disabled={loading}
            >
              <span className="icon">
                <i className="fas fa-save"></i>
              </span>
              <span>Criar Tarefa</span>
            </button>
          </div>
        </div>
      </form>
    </div>
  );
};

export default TaskForm;
