import { useState, useEffect, FormEvent } from 'react';
import TaskForm from './components/TaskForm';
import TaskList from './components/TaskList';
import { Task } from './types/Task';
import { getTasks, getExecutedTasks, updateTask } from './services/api';

const App: React.FC = () => {
  const [tasks, setTasks] = useState<Task[]>([]);
  const [executedTasks, setExecutedTasks] = useState<Task[]>([]);
  const [refreshCounter, setRefreshCounter] = useState(0);
  const [editingTask, setEditingTask] = useState<Task | null>(null);

  // Adicione estados para controle de erro
  const [serverError, setServerError] = useState<boolean>(false);
  const [retryCount, setRetryCount] = useState<number>(0);

  // Função para forçar a atualização dos dados
  const refreshData = () => {
    setRefreshCounter(prev => prev + 1);
  };

  // Usar o refreshCounter como dependência do useEffect
  useEffect(() => {
    const fetchTasks = async () => {
      try {
        console.log('Tentando conectar ao servidor...');
        setServerError(false);

        const pendingTasks = await getTasks();
        console.log('Tarefas pendentes recebidas:', pendingTasks.length);
        setTasks(pendingTasks.filter(task => task.status === 'pending'));

        console.log('Buscando tarefas executadas...');
        const executedTasks = await getExecutedTasks();
        console.log('Tarefas executadas recebidas:', executedTasks.length);
        if (executedTasks.length > 0) {
          console.log('Detalhes das tarefas executadas:', 
            executedTasks.map(t => `${t.id}: ${t.title} (${t.status})`).join(', '));
        }
        setExecutedTasks(executedTasks);

        // Reset retry count on success
        if (retryCount > 0) setRetryCount(0);
      } catch (error) {
        console.error('Error fetching tasks:', error);
        setServerError(true);

        // Increment retry count
        setRetryCount(prev => prev + 1);
      }
    };

    fetchTasks();

    // Atualizar a cada 1 segundo para capturar mudanças de status mais rapidamente
    const interval = setInterval(fetchTasks, 1000);
    return () => clearInterval(interval);
  }, [refreshCounter, retryCount]);

  const handleTaskCreated = () => {
    refreshData();
  };

  const handleTaskDeleted = () => {
    refreshData();
  };

  const handleEditTask = (task: Task) => {
    setEditingTask(task);
  };

  const handleCancelEdit = () => {
    setEditingTask(null);
  };

  const handleUpdateTask = async (updatedTask: Partial<Task>) => {
    if (!editingTask?.id) return;

    try {
      await updateTask(editingTask.id, updatedTask);
      setEditingTask(null);
      refreshData();
    } catch (error) {
      console.error('Failed to update task:', error);
    }
  };

  const handleSubmit = (e: FormEvent) => {
    e.preventDefault();
    const form = e.currentTarget as HTMLFormElement;

    const titleInput = form.elements.namedItem('title') as HTMLInputElement;
    const descriptionInput = form.elements.namedItem('description') as HTMLTextAreaElement;

    if (!titleInput || !descriptionInput) return;

    handleUpdateTask({
      title: titleInput.value,
      description: descriptionInput.value,
    });
  };

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <div className="max-w-6xl mx-auto">
        <h1 className="text-3xl font-bold mb-8 text-center">Task Scheduler</h1>

        {serverError && (
          <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4 text-center">
            <p className="font-bold">Erro de Conexão</p>
            <p className="text-sm">Não foi possível conectar ao servidor. Verifique se o servidor está em execução.</p>
            <button 
              onClick={() => setRefreshCounter(prev => prev + 1)}
              className="bg-red-500 hover:bg-red-700 text-white font-bold py-1 px-2 rounded mt-2 text-sm"
            >
              Tentar Novamente
            </button>
          </div>
        )}

        <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-8">
          <TaskForm onTaskCreated={handleTaskCreated} />

          {editingTask && (
            <div className="bg-white p-6 rounded-lg shadow-md">
              <h2 className="text-xl font-semibold mb-4">Edit Task</h2>
              <form onSubmit={handleSubmit}>
                <div className="mb-4">
                  <label htmlFor="title" className="block text-gray-700 mb-2">Title</label>
                  <input
                    id="title"
                    type="text"
                    name="title"
                    defaultValue={editingTask.title}
                    className="w-full px-3 py-2 border rounded-md"
                  />
                </div>

                <div className="mb-4">
                  <label htmlFor="description" className="block text-gray-700 mb-2">Description</label>
                  <textarea
                    id="description"
                    name="description"
                    defaultValue={editingTask.description || ''}
                    className="w-full px-3 py-2 border rounded-md"
                    rows={3}
                  />
                </div>

                <div className="flex space-x-2">
                  <button
                    type="submit"
                    className="bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-600"
                  >
                    Update
                  </button>
                  <button
                    type="button"
                    onClick={handleCancelEdit}
                    className="bg-gray-300 text-gray-700 px-4 py-2 rounded-md hover:bg-gray-400"
                  >
                    Cancel
                  </button>
                </div>
              </form>
            </div>
          )}
        </div>

        <div className="mb-8">
          <TaskList
            tasks={tasks}
            title="Pending Tasks"
            onTaskDeleted={handleTaskDeleted}
            onTaskEdit={handleEditTask}
          />
        </div>

        <div>
          <TaskList
            tasks={executedTasks}
            title={`Executed Tasks (${executedTasks.length})`} // Mostra a contagem
            onTaskDeleted={handleTaskDeleted}
          />
        </div>
      </div>
    </div>
  );
};

export default App;
