import React from 'react';
import { Task } from '../types/Task';

interface TaskItemProps {
  task: Task;
  onDelete: (id: number | undefined) => void;
  onEdit?: (task: Task) => void;
}

const TaskItem: React.FC<TaskItemProps> = ({ task, onDelete, onEdit }) => {
  const formatDateTime = (date: Date | undefined) => {
    if (!date) return 'N/A';
    return new Date(date).toLocaleString();
  };

  const getStatusBadge = (status: string) => {
    switch (status) {
      case 'pending':
        return <span className="bg-yellow-100 text-yellow-800 text-xs font-medium px-2.5 py-0.5 rounded">Pendente</span>;
      case 'executed':
        return <span className="bg-green-100 text-green-800 text-xs font-medium px-2.5 py-0.5 rounded">Executada</span>;
      case 'completed':
        return <span className="bg-green-100 text-green-800 text-xs font-medium px-2.5 py-0.5 rounded">Conclu√≠da</span>;
      default:
        return <span className="bg-gray-100 text-gray-800 text-xs font-medium px-2.5 py-0.5 rounded">{status}</span>;
    }
  };

  return (
    <tr>
      <td className="py-2 px-4 border-b">{task.title}</td>
      <td className="py-2 px-4 border-b">
        {task.type === 'one-time' ? 'One-time' : 'Recurring'}
        {task.type === 'recurring' && task.cronExpression && (
          <span className="block text-xs text-gray-500">
            {task.cronExpression}
          </span>
        )}
      </td>
      <td className="py-2 px-4 border-b">
        <div className="flex items-center">
          {getStatusBadge(task.status)}
          <span className="ml-2">{formatDateTime(task.scheduledTime)}</span>
        </div>
      </td>
      <td className="py-2 px-4 border-b">
        {formatDateTime(task.lastExecutionTime)}
      </td>
      <td className="py-2 px-4 border-b">
        <div className="flex space-x-2">
          {onEdit && (
            <button
              onClick={() => onEdit(task)}
              className="text-blue-500 hover:text-blue-700"
            >
              Edit
            </button>
          )}
          <button
            onClick={() => onDelete(task.id)}
            className="text-red-500 hover:text-red-700"
          >
            Delete
          </button>
        </div>
      </td>
    </tr>
  );
};

export default TaskItem;
