import React, { useState } from 'react';
import { Task, TaskFormData } from '../types/Task';
import { createTask } from '../services/api';

interface TaskFormProps {
  onTaskCreated: (task?: Task) => void;
}

const TaskForm: React.FC<TaskFormProps> = ({ onTaskCreated }) => {
  const [formData, setFormData] = useState<TaskFormData>({
    title: '',
    description: '',
    type: 'one-time',
    scheduledTime: undefined,
    cronExpression: '',
  });

  const [error, setError] = useState<string | null>(null);
  const [loading, setLoading] = useState<boolean>(false);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    setFormData((prev: TaskFormData) => ({ ...prev, [name]: value }));
  };

  const handleDateTimeChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { value } = e.target;
    setFormData((prev: TaskFormData) => ({ ...prev, scheduledTime: value ? new Date(value) : undefined }));
  };

  const handleSubmit = async (event: React.FormEvent) => {
    event.preventDefault();
    setError(null);
    setLoading(true);

    try {
      if (!formData.title) {
        setError('O título é obrigatório');
        return;
      }

      if (formData.type === 'one-time' && !formData.scheduledTime) {
        setError('O horário agendado é obrigatório para tarefas únicas');
        return;
      }

      if (formData.type === 'recurring' && !formData.cronExpression) {
        setError('A expressão cron é obrigatória para tarefas recorrentes');
        return;
      }

      let processedData = { ...formData };
      const createdTask = await createTask(processedData);

      onTaskCreated(createdTask);

      // Limpar o formulário
      setFormData({
        title: '',
        description: '',
        type: 'one-time',
        scheduledTime: new Date(),
        cronExpression: '',
      });
    } catch (error) {
      console.error("Erro ao criar tarefa:", error);
      setError('Falha ao criar tarefa. Por favor, tente novamente.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="bg-white p-6 rounded-lg shadow-lg border border-indigo-100">
      <h2 className="text-xl font-semibold mb-4 text-indigo-700 flex items-center">
        <svg className="h-5 w-5 mr-2" fill="none" viewBox="0 0 24 24" stroke="currentColor">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 6v6m0 0v6m0-6h6m-6 0H6" />
        </svg>
        Nova Tarefa
      </h2>

      {error && (
        <div className="bg-red-50 border-l-4 border-red-400 text-red-700 p-4 mb-4 rounded">
          <div className="flex">
            <svg className="h-5 w-5 mr-2" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-3L13.732 4c-.77-1.333-2.694-1.333-3.464 0L3.34 16c-.77 1.333.192 3 1.732 3z" />
            </svg>
            <span>{error}</span>
          </div>
        </div>
      )}

      <form onSubmit={handleSubmit} className="space-y-4">
        <div>
          <label className="block text-gray-700 mb-2 font-medium">Título</label>
          <input
            type="text"
            name="title"
            value={formData.title}
            onChange={handleChange}
            className="w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:border-transparent transition"
            placeholder="Digite o título da tarefa"
          />
        </div>

        <div>
          <label className="block text-gray-700 mb-2 font-medium">Descrição</label>
          <textarea
            name="description"
            value={formData.description || ''}
            onChange={handleChange}
            className="w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:border-transparent transition"
            rows={3}
            placeholder="Descreva a tarefa (opcional)"
          />
        </div>

        <div>
          <label className="block text-gray-700 mb-2 font-medium">Tipo de Tarefa</label>
          <select
            name="type"
            value={formData.type}
            onChange={handleChange}
            className="w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:border-transparent transition"
          >
            <option value="one-time">Única</option>
            <option value="recurring">Recorrente</option>
          </select>
        </div>

        {formData.type === 'one-time' && (
          <div>
            <label className="block text-gray-700 mb-2 font-medium">Horário Agendado</label>
            <input
              type="datetime-local"
              name="scheduledTime"
              value={formData.scheduledTime ? new Date(formData.scheduledTime.getTime() - formData.scheduledTime.getTimezoneOffset() * 60000).toISOString().slice(0, 16) : ''}
              onChange={handleDateTimeChange}
              className="w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:border-transparent transition"
            />
          </div>
        )}

        {formData.type === 'recurring' && (
          <div>
            <label className="block text-gray-700 mb-2 font-medium">Expressão Cron</label>
            <input
              type="text"
              name="cronExpression"
              value={formData.cronExpression || ''}
              onChange={handleChange}
              placeholder="* * * * *"
              className="w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:border-transparent transition"
            />
            <p className="text-sm text-gray-500 mt-1">
              Formato: minuto hora dia-do-mês mês dia-da-semana
            </p>
          </div>
        )}

        <button
          type="submit"
          disabled={loading}
          className={`w-full bg-indigo-600 text-white py-2 px-4 rounded-md hover:bg-indigo-700 transition duration-200 flex items-center justify-center ${loading ? 'opacity-70 cursor-not-allowed' : ''}`}
        >
          {loading ? (
            <>
              <svg className="animate-spin -ml-1 mr-3 h-5 w-5 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
              </svg>
              Criando...
            </>
          ) : (
            <>
              <svg className="h-5 w-5 mr-2" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 6v6m0 0v6m0-6h6m-6 0H6" />
              </svg>
              Criar Tarefa
            </>
          )}
        </button>
      </form>
    </div>
  );
};

export default TaskForm;
