import React, { useState } from 'react';
import { Task, TaskFormData } from '../types/Task'; // Adicionar importação de Task
import { createTask } from '../services/api';

// Atualizar a interface para aceitar um argumento task opcional
interface TaskFormProps {
  onTaskCreated: (task?: Task) => void;
}

const TaskForm: React.FC<TaskFormProps> = ({ onTaskCreated }) => {
  const [formData, setFormData] = useState<TaskFormData>({
    title: '',
    description: '',
    type: 'one-time',
    scheduledTime: undefined,
    cronExpression: '',
  });

  const [error, setError] = useState<string | null>(null);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    setFormData((prev: TaskFormData) => ({ ...prev, [name]: value }));
  };

  const handleDateTimeChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { value } = e.target;
    setFormData((prev: TaskFormData) => ({ ...prev, scheduledTime: value ? new Date(value) : undefined }));
  };

  const handleSubmit = async (event: React.FormEvent) => {
    event.preventDefault();
    setError(null);

    try {
      console.log('Enviando dados do formulário:', formData);

      if (!formData.title) {
        setError('Title is required');
        return;
      }

      if (formData.type === 'one-time' && !formData.scheduledTime) {
        setError('Scheduled time is required for one-time tasks');
        return;
      }

      if (formData.type === 'recurring' && !formData.cronExpression) {
        setError('Cron expression is required for recurring tasks');
        return;
      }

      // Garanta que scheduledTime é uma string ISO válida
      let processedData = { ...formData };
      if (formData.type === 'one-time' && formData.scheduledTime) {
        // Se já for uma string ISO, mantenha-a, caso contrário, converta
      }

      console.log("Dados processados:", processedData);
      const createdTask = await createTask(processedData);

      console.log("Tarefa criada:", createdTask);
      onTaskCreated(createdTask); // Agora é válido passar a tarefa criada

      // Limpar o formulário
      setFormData({
        title: '',
        description: '',
        type: 'one-time',
        scheduledTime: new Date(), // Use objeto Date aqui
        cronExpression: '',
      });
    } catch (error) {
      console.error("Erro ao criar tarefa:", error);
      console.error("Erro detalhado:", error);
      setError('Failed to create task. Please try again.');
    }
  };

  return (
    <div className="bg-white p-6 rounded-lg shadow-md">
      <h2 className="text-xl font-semibold mb-4">Create New Task</h2>

      {error && (
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
          {error}
        </div>
      )}

      <form onSubmit={handleSubmit}>
        <div className="mb-4">
          <label className="block text-gray-700 mb-2">Title</label>
          <input
            type="text"
            name="title"
            value={formData.title}
            onChange={handleChange}
            className="w-full px-3 py-2 border rounded-md"
          />
        </div>

        <div className="mb-4">
          <label className="block text-gray-700 mb-2">Description</label>
          <textarea
            name="description"
            value={formData.description || ''}
            onChange={handleChange}
            className="w-full px-3 py-2 border rounded-md"
            rows={3}
          />
        </div>

        <div className="mb-4">
          <label className="block text-gray-700 mb-2">Task Type</label>
          <select
            name="type"
            value={formData.type}
            onChange={handleChange}
            className="w-full px-3 py-2 border rounded-md"
          >
            <option value="one-time">One-time</option>
            <option value="recurring">Recurring</option>
          </select>
        </div>

        {formData.type === 'one-time' && (
          <div className="mb-4">
            <label className="block text-gray-700 mb-2">Scheduled Time</label>
            <input
              type="datetime-local"
              name="scheduledTime"
              value={formData.scheduledTime ? new Date(formData.scheduledTime.getTime() - formData.scheduledTime.getTimezoneOffset() * 60000).toISOString().slice(0, 16) : ''}
              onChange={handleDateTimeChange}
              className="w-full px-3 py-2 border rounded-md"
            />
          </div>
        )}

        {formData.type === 'recurring' && (
          <div className="mb-4">
            <label className="block text-gray-700 mb-2">Cron Expression</label>
            <input
              type="text"
              name="cronExpression"
              value={formData.cronExpression || ''}
              onChange={handleChange}
              placeholder="* * * * *"
              className="w-full px-3 py-2 border rounded-md"
            />
            <p className="text-sm text-gray-500 mt-1">
              Format: minute hour day-of-month month day-of-week
            </p>
          </div>
        )}

        <button
          type="submit"
          className="bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-600"
        >
          Create Task
        </button>
      </form>
    </div>
  );
};

export default TaskForm;
