import { useState, useEffect, FormEvent } from 'react';
import TaskForm from './components/TaskForm';
import TaskList from './components/TaskList';
import { Task } from './types/Task';
import { getTasks, getExecutedTasks, updateTask } from './services/api';

const App: React.FC = () => {
  const [tasks, setTasks] = useState<Task[]>([]);
  const [executedTasks, setExecutedTasks] = useState<Task[]>([]);
  const [refreshCounter, setRefreshCounter] = useState(0);
  const [editingTask, setEditingTask] = useState<Task | null>(null);
  const [serverError, setServerError] = useState<boolean>(false);
  const [retryCount, setRetryCount] = useState<number>(0);
  const [loading, setLoading] = useState<boolean>(true);

  // Função para forçar a atualização dos dados
  const refreshData = () => {
    setRefreshCounter(prev => prev + 1);
  };

  useEffect(() => {
    const fetchTasks = async () => {
      try {
        setLoading(true);
        setServerError(false);

        const pendingTasks = await getTasks();
        setTasks(pendingTasks.filter(task => task.status === 'pending'));

        const executedTasks = await getExecutedTasks();
        setExecutedTasks(executedTasks);

        if (retryCount > 0) setRetryCount(0);
      } catch (error) {
        console.error('Error fetching tasks:', error);
        setServerError(true);
        setRetryCount(prev => prev + 1);
      } finally {
        setLoading(false);
      }
    };

    fetchTasks();

    const interval = setInterval(fetchTasks, 5000);
    return () => clearInterval(interval);
  }, [refreshCounter, retryCount]);

  const handleTaskCreated = () => {
    refreshData();
  };

  const handleTaskDeleted = () => {
    refreshData();
  };

  const handleEditTask = (task: Task) => {
    setEditingTask(task);
  };

  const handleCancelEdit = () => {
    setEditingTask(null);
  };

  const handleUpdateTask = async (updatedTask: Partial<Task>) => {
    if (!editingTask?.id) return;

    try {
      await updateTask(editingTask.id, updatedTask);
      setEditingTask(null);
      refreshData();
    } catch (error) {
      console.error('Failed to update task:', error);
    }
  };

  const handleSubmit = (e: FormEvent) => {
    e.preventDefault();
    const form = e.currentTarget as HTMLFormElement;

    const titleInput = form.elements.namedItem('title') as HTMLInputElement;
    const descriptionInput = form.elements.namedItem('description') as HTMLTextAreaElement;

    if (!titleInput || !descriptionInput) return;

    handleUpdateTask({
      title: titleInput.value,
      description: descriptionInput.value,
    });
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-50 to-blue-100 p-6">
      <div className="max-w-6xl mx-auto">
        <header className="text-center mb-12">
          <h1 className="text-4xl font-bold text-indigo-800 mb-2">Task Scheduler</h1>
          <p className="text-gray-600">Organize suas tarefas com eficiência</p>
        </header>

        {serverError && (
          <div className="bg-red-100 border-l-4 border-red-500 text-red-700 p-4 rounded-lg mb-6 shadow-md animate-pulse">
            <div className="flex items-center">
              <svg className="h-6 w-6 text-red-500 mr-3" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-3L13.732 4c-.77-1.333-2.694-1.333-3.464 0L3.34 16c-.77 1.333.192 3 1.732 3z" />
              </svg>
              <div>
                <p className="font-bold">Erro de Conexão</p>
                <p className="text-sm">Não foi possível conectar ao servidor. Verifique se o servidor está em execução.</p>
              </div>
            </div>
            <button 
              onClick={() => setRefreshCounter(prev => prev + 1)}
              className="mt-3 bg-red-500 hover:bg-red-600 text-white font-medium py-2 px-4 rounded-md transition duration-200 ease-in-out transform hover:-translate-y-1 hover:shadow-lg"
            >
              Tentar Novamente
            </button>
          </div>
        )}

        {loading && !serverError && (
          <div className="flex justify-center items-center mb-6">
            <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-indigo-500"></div>
            <span className="ml-3 text-indigo-600">Carregando tarefas...</span>
          </div>
        )}

        <div className="grid grid-cols-1 md:grid-cols-2 gap-8 mb-10">
          <div className="transform transition duration-300 hover:scale-102 hover:shadow-xl">
            <TaskForm onTaskCreated={handleTaskCreated} />
          </div>

          {editingTask && (
            <div className="bg-white p-6 rounded-lg shadow-lg border border-indigo-100">
              <h2 className="text-xl font-semibold mb-4 text-indigo-700 flex items-center">
                <svg className="h-5 w-5 mr-2" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M11 5H6a2 2 0 00-2 2v11a2 2 0 002 2h11a2 2 0 002-2v-5m-1.414-9.414a2 2 0 112.828 2.828L11.828 15H9v-2.828l8.586-8.586z" />
                </svg>
                Editar Tarefa
              </h2>
              <form onSubmit={handleSubmit} className="space-y-4">
                <div>
                  <label htmlFor="title" className="block text-gray-700 mb-2 font-medium">Título</label>
                  <input
                    id="title"
                    type="text"
                    name="title"
                    defaultValue={editingTask.title}
                    className="w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:border-transparent transition"
                    required
                  />
                </div>

                <div>
                  <label htmlFor="description" className="block text-gray-700 mb-2 font-medium">Descrição</label>
                  <textarea
                    id="description"
                    name="description"
                    defaultValue={editingTask.description || ''}
                    className="w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:border-transparent transition"
                    rows={3}
                  />
                </div>

                <div className="flex space-x-3">
                  <button
                    type="submit"
                    className="bg-indigo-600 text-white px-5 py-2 rounded-md hover:bg-indigo-700 transition duration-200 flex items-center"
                  >
                    <svg className="h-4 w-4 mr-2" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M5 13l4 4L19 7" />
                    </svg>
                    Atualizar
                  </button>
                  <button
                    type="button"
                    onClick={handleCancelEdit}
                    className="bg-gray-200 text-gray-700 px-5 py-2 rounded-md hover:bg-gray-300 transition duration-200 flex items-center"
                  >
                    <svg className="h-4 w-4 mr-2" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12" />
                    </svg>
                    Cancelar
                  </button>
                </div>
              </form>
            </div>
          )}
        </div>

        <div className="mb-10 transform transition duration-300 hover:shadow-xl">
          <TaskList
            tasks={tasks}
            title="Tarefas Pendentes"
            onTaskDeleted={handleTaskDeleted}
            onTaskEdit={handleEditTask}
          />
        </div>

        <div className="transform transition duration-300 hover:shadow-xl">
          <TaskList
            tasks={executedTasks}
            title={`Tarefas Executadas (${executedTasks.length})`}
            onTaskDeleted={handleTaskDeleted}
          />
        </div>

        <footer className="mt-12 text-center text-gray-500 text-sm">
          <p>© {new Date().getFullYear()} Task Scheduler - Desenvolvido com ❤️</p>
        </footer>
      </div>
    </div>
  );
};

export default App;
