import React from 'react';
import { Task } from '../types/Task';
import { format } from 'date-fns';
import { ptBR } from 'date-fns/locale';

interface TaskItemProps {
  task: Task;
  onDelete: (id: number | undefined) => void;
  onEdit?: (task: Task) => void;
}

const TaskItem: React.FC<TaskItemProps> = ({ task, onDelete, onEdit }) => {
  const formatDate = (date: Date | string | undefined) => {
    if (!date) return '-';
    try {
      return format(new Date(date), 'dd/MM/yyyy HH:mm', { locale: ptBR });
    } catch (e) {
      console.error('Error formatting date:', e);
      return 'Data inválida';
    }
  };

  const getStatusClass = (status: string) => {
    switch (status) {
      case 'pending':
        return 'bg-yellow-100 text-yellow-800 border-yellow-200';
      case 'executed':
        return 'bg-green-100 text-green-800 border-green-200';
      case 'failed':
        return 'bg-red-100 text-red-800 border-red-200';
      default:
        return 'bg-gray-100 text-gray-800 border-gray-200';
    }
  };

  const getTypeLabel = (type: string) => {
    return type === 'one-time' ? 'Única' : 'Recorrente';
  };

  const getStatusLabel = (status: string) => {
    switch (status) {
      case 'pending':
        return 'Pendente';
      case 'executed':
        return 'Executada';
      case 'failed':
        return 'Falhou';
      default:
        return status;
    }
  };

  return (
    <tr>
      <td>
        <div className="has-text-weight-medium">{task.title}</div>
        {task.description && (
          <div className="has-text-grey is-size-7">{task.description}</div>
        )}
      </td>
      <td>
        <span className={`tag ${task.type === 'one-time' ? 'is-info' : 'is-warning'}`}>
          {task.type === 'one-time' ? 'Única' : 'Recorrente'}
        </span>
      </td>
      <td>
        <span className={`tag ${task.status === 'pending' ? 'is-warning' : 'is-success'}`}>
          {task.status === 'pending' ? 'Pendente' : 'Executada'}
        </span>
        <br />
        <small className="has-text-grey">
          {task.type === 'one-time' 
            ? formatDate(task.scheduledTime)
            : task.cronExpression
          }
        </small>
      </td>
      <td>
        {task.lastExecutionTime ? (
          <span className="has-text-grey">
            {formatDate(task.lastExecutionTime)}
          </span>
        ) : (
          <span className="has-text-grey-light">-</span>
        )}
      </td>
      <td>
        <div className="buttons are-small">
          <button 
            className="button is-danger is-light"
            onClick={() => onDelete(task.id)}
          >
            <span className="icon">
              <i className="fas fa-trash"></i>
            </span>
          </button>
        </div>
      </td>
    </tr>
  );
};

export default TaskItem;
