import React from 'react';
import { Task } from '../types/Task';
import { deleteTask } from '../services/api';

interface TaskListProps {
  tasks: Task[];
  title: string;
  onTaskDeleted: () => void;
  onTaskEdit?: (task: Task) => void;
}

const TaskList: React.FC<TaskListProps> = ({ tasks, title, onTaskDeleted, onTaskEdit }) => {
  const handleDelete = async (id: number | undefined) => {
    if (!id) return;

    try {
      await deleteTask(id);
      onTaskDeleted();
    } catch (error) {
      console.error('Failed to delete task:', error);
    }
  };

  const formatDateTime = (date: Date | undefined) => {
    if (!date) return 'N/A';
    return new Date(date).toLocaleString();
  };

  return (
    <div className="bg-white p-6 rounded-lg shadow-md">
      <h2 className="text-xl font-semibold mb-4">{title}</h2>

      {tasks.length === 0 ? (
        <p className="text-gray-500">No tasks found.</p>
      ) : (
        <div className="overflow-x-auto">
          <table className="min-w-full bg-white">
            <thead>
              <tr>
                <th className="py-2 px-4 border-b text-left">Title</th>
                <th className="py-2 px-4 border-b text-left">Type</th>
                <th className="py-2 px-4 border-b text-left">Scheduled Time</th>
                <th className="py-2 px-4 border-b text-left">Status</th>
                <th className="py-2 px-4 border-b text-left">Last Execution</th>
                <th className="py-2 px-4 border-b text-left">Actions</th>
              </tr>
            </thead>
            <tbody>
              {tasks.map(task => (
                <tr key={task.id}>
                  <td className="py-2 px-4 border-b">{task.title}</td>
                  <td className="py-2 px-4 border-b">
                    {task.type === 'one-time' ? 'One-time' : 'Recurring'}
                    {task.type === 'recurring' && task.cronExpression && (
                      <span className="block text-xs text-gray-500">
                        {task.cronExpression}
                      </span>
                    )}
                  </td>
                  <td className="py-2 px-4 border-b">
                    {task.type === 'one-time' 
                      ? formatDateTime(task.scheduledTime)
                      : 'Recurring'}
                  </td>
                  <td className="py-2 px-4 border-b">
                    <span className={`px-2 py-1 rounded-full text-xs ${
                      task.status === 'executed' 
                        ? 'bg-green-100 text-green-800' 
                        : task.status === 'failed'
                        ? 'bg-red-100 text-red-800'
                        : 'bg-yellow-100 text-yellow-800'
                    }`}>
                      {task.status}
                    </span>
                  </td>
                  <td className="py-2 px-4 border-b">
                    {formatDateTime(task.lastExecutionTime)}
                  </td>
                  <td className="py-2 px-4 border-b">
                    <div className="flex space-x-2">
                      {onTaskEdit && (
                        <button
                          onClick={() => onTaskEdit(task)}
                          className="text-blue-500 hover:text-blue-700"
                        >
                          Edit
                        </button>
                      )}
                      <button
                        onClick={() => handleDelete(task.id)}
                        className="text-red-500 hover:text-red-700"
                      >
                        Delete
                      </button>
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default TaskList;
