import React from 'react';
import { Task } from '../types/Task';
import { deleteTask } from '../services/api';
import TaskItem from './TaskItem';

interface TaskListProps {
  tasks: Task[];
  title: string;
  onTaskDeleted: () => void;
  onTaskEdit?: (task: Task) => void;
}

const TaskList: React.FC<TaskListProps> = ({ tasks, title, onTaskDeleted, onTaskEdit }) => {
  const handleDelete = async (id: number | undefined) => {
    if (!id) return;

    if (!window.confirm('Tem certeza que deseja excluir esta tarefa?')) {
      return;
    }

    try {
      await deleteTask(id);
      onTaskDeleted();
    } catch (error) {
      console.error('Failed to delete task:', error);
      alert('Erro ao excluir a tarefa. Por favor, tente novamente.');
    }
  };

  return (
    <div className="bg-white p-6 rounded-lg shadow-lg border border-indigo-100">
      <h2 className="text-xl font-semibold mb-4 text-indigo-700 flex items-center">
        {title.includes('Pendentes') ? (
          <svg className="h-5 w-5 mr-2" fill="none" viewBox="0 0 24 24" stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z" />
          </svg>
        ) : (
          <svg className="h-5 w-5 mr-2" fill="none" viewBox="0 0 24 24" stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" />
          </svg>
        )}
        {title}
      </h2>

      {tasks.length === 0 ? (
        <div className="bg-gray-50 p-8 rounded-md text-center">
          <svg className="h-12 w-12 mx-auto text-gray-400 mb-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 5H7a2 2 0 00-2 2v12a2 2 0 002 2h10a2 2 0 002-2V7a2 2 0 00-2-2h-2M9 5a2 2 0 002 2h2a2 2 0 002-2M9 5a2 2 0 012-2h2a2 2 0 012 2" />
          </svg>
          <p className="text-gray-500 text-lg">Nenhuma tarefa encontrada.</p>
        </div>
      ) : (
        <div className="overflow-x-auto">
          <table className="min-w-full bg-white rounded-lg overflow-hidden">
            <thead className="bg-indigo-50 text-indigo-800">
              <tr>
                <th className="py-3 px-4 text-left font-medium">Título</th>
                <th className="py-3 px-4 text-left font-medium">Tipo</th>
                <th className="py-3 px-4 text-left font-medium">Status/Tempo</th>
                <th className="py-3 px-4 text-left font-medium">Última Execução</th>
                <th className="py-3 px-4 text-left font-medium">Ações</th>
              </tr>
            </thead>
            <tbody className="divide-y divide-gray-100">
              {tasks.map(task => (
                <TaskItem 
                  key={task.id} 
                  task={task} 
                  onDelete={handleDelete}
                  onEdit={onTaskEdit}
                />
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default TaskList;
